import React/*, { useContext }*/ from "react";
import "./css/Shopcategory.css";
// import ShopContext from "../context/ShopContext";
import dropdown_icon from '../components/Assets/dropdown_icon.png';
import all_product from "../components/Assets/all_product.js"; // You don't need this import
import Item from "../components/item/Item";

const ShopCategory = (props) => {
    // const { all_product } = useContext(ShopContext);

    return (
        <div className="Shop-Category">
            <img src={props.banner} alt="Banner" /> {/* Added alt prop */}
            <div className="Shopcatergory-indexsort">
                <p>
                    <span>
                        Showing 1-12
                    </span> out of 36 products
                </p>

                <div className="Shopcatergory-Sort">
                    Sort By<img src={dropdown_icon} alt="Dropdown Icon" /> {/* Added alt prop */}
                </div>
            </div>
            <div className="shopcategory-products">
                {all_product.map((item, i) => {
                    if (props.category === item.category) {
                        return <Item key={i} id={item.id} name={item.name} image={item.image} new_price={item.new_price} old_price={item.old_price} />;
                    }
                    else {
                        return null;
                    }
                })}
            </div> 
        </div>
    );
};

export default ShopCategory;
